  $(".pgStep__users-stats-marker").css("opacity", 1.0);

  switch (markerLeft) {
    case 0.3:
      markerLeft = 0.125;
    break;
    case 0.4:
      markerLeft = 0.375;
    break;
    case 0.5:
      markerLeft = 0.5;
    break;
    case 0.6:
      markerLeft = 0.625;
    break;
    case 0.7:
      markerLeft = 0.875;
    break;
  }

  $($(".pgStep__users-stats-marker")[0]).css("left", (markerLeft * 100) + "%");

  var offsetX = 2.5;

  if (markerTop > 10) {
    offsetX = -2.5;
  }
  else if (markerTop > 10) {
    offsetX = 2.5;
  }
  else {
    offsetX = 0;
  }

  $($(".pgStep__users-stats-marker")[1]).css("left", ((((markerTop + offsetX) * 5))) + "%");

  $('.pgConclusions-sharebar-wrapper').css("visibility", "visible");
