function resizeend() {
    if (new Date() - rtime < delta) {
        setTimeout(main.resizeend, delta);
    } else {
      timeout = false;
      //setupCanvas(); 
      if ($(".pgArticle").width() < tabletTreshold) {
        $(".pgChart").scrollLeft(($(".pgArticle").width() / oldWidth) * scrollLeft);
      }
    }
}

